import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaRegUser } from "react-icons/fa";
import { FaRegHeart } from "react-icons/fa";
import { PiShoppingCartSimple } from "react-icons/pi";
import { TiDeleteOutline } from "react-icons/ti";
import { useDispatch, useSelector } from "react-redux";
import { useAuth } from "../context/AuthContext";
import { LuLogOut } from "react-icons/lu";
import { searchProduct } from "../../redux/reducers/productSlice";


const Header = () => {
  const { register, signInWithGoogle, user, logOut, LogIn } = useAuth();
  const {basket} = useSelector((s) => s.bas)
  const [modal, setModal] = useState(false);
  const [userEmail, setUserEmail] = useState("");
  const [userPass, setUserPass] = useState("");
  const [error, setError] = useState("");
  const [inputSearch,setInputSearch] = useState('')
  const dispatch = useDispatch();
  const nav = useNavigate();
  console.log(user ,'user');
  

  async function handleRegister() {
    try {
      await register(userEmail, userPass);
      setUserEmail("");
      setUserPass("");
      setModal(false);
      nav("/addProduct");
    } catch (err) {
      setError(err.message);
    }
  }
  async function handleGoogle() {
    try {
      await signInWithGoogle();
      setModal(false);
      nav("/addProduct");
    } catch (err) {
      setError(err.message);
    }
  }

  async function handleLogIn() {
    try {
      await LogIn(userEmail, userPass);
      setUserEmail("");
      setUserPass("");
      setModal(false);
      nav("/addProduct");
    } catch (err) {
      setError(err.message);
    }
  }

  return (
    <div className=" z-[99] bg-gray-300 border-b-[20px] border-b-slate-100 ">
      <div className="conteiner">
        <div className="flex items-center justify-between">
          <img onClick={() => nav('/addProduct')}
            src={`https://t3.ftcdn.net/jpg/05/51/08/60/360_F_551086071_xZWK3iZCmytQxEONnah61hEgZh2iVB02.png`}
            alt="img"
            width={120}
          />
          <div className="flex items-center justify-between gap-[80px] text-2xl">
            <Link to={"/"}>HOME</Link>
            <Link to={"/product"}>PRODUCTS</Link>
            <Link to={"/sale"}>SALE</Link>
          </div>
          <div className="flex items-center justify-between gap-[40px] text-3xl relative">
          <input onChange={(e) => setInputSearch(e.target.value)}
              onInput={() => {
                dispatch(searchProduct(inputSearch));
                nav("/search");
                setInputSearch("");
              }}
              type="text" placeholder="search...." className="py-[10px] px-[30px] text-xl bg-transparent border-2 border-black rounded-3xl"  />
          <a onClick={() => nav('basket')} className="text-4xl">
              <PiShoppingCartSimple />
            </a>
            <div onClick={() => setModal(true)} className="flex items-center flex-col">
        
           <img
              src={
                user
                  ? user.photoURL ? user.photoURL 
                   : `data:image/png;base64,`
                  : `data:image/png;base64,`
              }
              alt="img"
              className=" sm:w-[35px]   rounded-[50%]"
            />
            <h1 className="sm:text-sm  xl:text-xl">
              {user ? user.displayName ? user.displayName : 'User Name' : "User"}
              </h1>
            </div>
          
            {/* <a>
              <FaRegHeart />
            </a> */}
           
            {
              basket.length ? <h1 className="absolute top-[-5px] right-[30%] text-sm text-white font-bold bg-black px-[5px] rounded-full">{basket.length}</h1>
              : null
            }
             {!user ? null : (
            <a
              onClick={() => logOut()}
              className="absolute top-[-15px] right-[-80px] text-3xl text-black font-bold"
            >
              <LuLogOut />{" "}
            </a>
          )}
          </div>

          {modal ? (
            <div className="">
              <div className="fixed top-[200px] left-[32%]  bg-slate-300 w-[560px] h-[540px] z-50 rounded-3xl px-[20px] flex items-center justify-center flex-col gap-[20px] border-t-[70px] border-t-gray-500">
                <a
                  onClick={() => setModal(false)}
                  className="absolute top-[-50px] right-5 text-3xl text-white"
                >
                  {" "}
                  <TiDeleteOutline />{" "}
                </a>
                <div className="mb-5 flex items-center flex-col w-[80%]">
                    <label
                      htmlFor="email"
                      className="block mb-2 text-2xl font-medium text-gray-900 "
                    >
                      USER EMAIL
                    </label>
                    <input   onChange={(e) => setUserEmail(e.target.value)}
                    value={userEmail}
                      type="text"
                      class="bg-gray-50 border border-gray-300 text-gray-900 text-2xl rounded-lg w-full p-4 dark:bg-gray-500 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white "
                      required
                    />
                  </div>
                  <div className="mb-5 flex items-center flex-col w-[80%]">
                    <label
                      htmlFor="email"
                      className="block mb-2 text-2xl font-medium text-gray-900 "
                    >
                      USER PASSWORD
                    </label>
                    <input    onChange={(e) => setUserPass(e.target.value)}
                    value={userPass}
                      type="password"
                      class="bg-gray-50 border border-gray-300 text-gray-900 text-2xl rounded-lg w-full p-4 dark:bg-gray-500 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white "
                      required
                    />
                  </div>
                <div className="flex items-center md:gap-[10px] sm:gap-[5px]">
                  <button
                    onClick={handleLogIn}
                    type="button"
                    className="text-white bg-gray-700 hover:bg-gray-900/90 focus:ring-4 focus:outline-none focus:ring-gray-900/50 font-medium rounded-lg md:text-xl sm:text-sm px-5 py-2.5 text-center inline-flex items-center dark:focus:ring-[#4285F4]/55 me-2 mb-2"
                  >
                    Log In
                  </button>
                  <button
                    onClick={handleRegister}
                    type="button"
                    className="text-white bg-gray-700 hover:bg-gray-900/90 focus:ring-4 focus:outline-none focus:ring-gray-900/50 font-medium rounded-lg md:text-xl sm:text-sm px-5 py-2.5 text-center inline-flex items-center dark:focus:ring-[#4285F4]/55 me-2 mb-2"
                  >
                    Register
                  </button>

                  <button
                    onClick={handleGoogle}
                    type="button"
                    className="text-white bg-gray-700 hover:bg-gray-900/90 focus:ring-4 focus:outline-none focus:ring-gray-600/50 font-medium rounded-lg md:text-xl sm:text-sm px-5 py-2.5 text-center inline-flex items-center dark:focus:ring-[#4285F4]/55 me-2 mb-2"
                  >
                    <svg
                      className="w-4 h-4 me-2"
                      aria-hidden="true"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="currentColor"
                      viewBox="0 0 18 19"
                    >
                      <path
                        fillRule="evenodd"
                        d="M8.842 18.083a8.8 8.8 0 0 1-8.65-8.948 8.841 8.841 0 0 1 8.8-8.652h.153a8.464 8.464 0 0 1 5.7 2.257l-2.193 2.038A5.27 5.27 0 0 0 9.09 3.4a5.882 5.882 0 0 0-.2 11.76h.124a5.091 5.091 0 0 0 5.248-4.057L14.3 11H9V8h8.34c.066.543.095 1.09.088 1.636-.086 5.053-3.463 8.449-8.4 8.449l-.186-.002Z"
                        clipRule="evenodd"
                      />
                    </svg>
                    Sign in with Google
                  </button>
                </div>
                {error.length ? (
                  <div
                    class="flex items-center p-4 mb-4 text-sm text-red-800 rounded-lg bg-red-50 dark:bg-gray-800 dark:text-red-400"
                    role="alert"
                  >
                    <svg
                      class="flex-shrink-0 inline w-4 h-4 me-3"
                      aria-hidden="true"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                    >
                      <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5ZM9.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM12 15H8a1 1 0 0 1 0-2h1v-3H8a1 1 0 0 1 0-2h2a1 1 0 0 1 1 1v4h1a1 1 0 0 1 0 2Z" />
                    </svg>
                    <span class="sr-only">Info</span>
                    <div>
                      <span class="font-medium">{error}</span>
                    </div>
                  </div>
                ) : null}
              </div>
              <div onClick={() => setModal(false)} className="bg"></div>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
};

export default Header;
